mapper.update:
UPDATE system_config sc


        sc.NAME = #{name},


        sc.KEY_WORD = #{keyWord}


        sc.NUM = #{num}


        sc.TYPE = #{type}


WHERE sc.ID = #{id}

mapper.select:
SELECT sc.ID AS id, sc.NAME AS name, sc.KEY_WORD AS keyWord, sc.NUM AS num, sc.TYPE AS type FROM system_config sc WHERE 1=1
AND sc.NAME = #{name}
AND sc.ID = #{id}
AND sc.TYPE in (${type})
AND sc.KEY_WORD = #{keyWord}

SELECT stc.ID AS id, stc.NAME AS name, stc.PLATFORM AS platform, stc.ACCESS_KEY_ID AS accessKeyId, stc.ACCESS_KEY_SECRET AS accessKeySecret, stc.ENDPOINT AS endpoint, stc.BACKET_NAME AS backetName, stc.ACCESS_TOKEN AS accessToken, DATE_FORMAT( stc.UPDATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS updateTime, stc.RETURN_URL AS returnUrl FROM system_tpp_config stc WHERE 1=1
AND stc.PLATFORM = #{platform}
AND stc.ID = #{id}
