mapper.select:
SELECT XC.ID AS id, XC.PARENT_ID AS parentId, XC. NAME AS name, XC.PARAMETER AS parameter, XC.REMARKS AS remarks, XC.CREATE_USER AS createUserId, cu.NICK_NAME AS createUserName, XC.LAST_CREATE_USER AS lastCreateUserId, lcu.NICK_NAME AS lastCreateUserName, DATE_FORMAT( XC.CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS createTime, DATE_FORMAT( XC.LAST_CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS lastcreateTime FROM XH_CLASSIFY XC LEFT JOIN system_administrator cu ON cu.ID = XC.CREATE_USER LEFT JOIN system_administrator lcu ON lcu.ID = XC.LAST_CREATE_USER WHERE 1=1
AND XC.ID = #{id}
AND XC.PARENT_ID = #{parentId}
AND (XC.PARENT_ID IS NULL or XC.PARENT_ID = '')
