mapper.select:
SELECT XGM.ID AS id, XGM. CODE AS code, XGM.SHOP_ID AS shopId, XSM. NAME AS shopName, XGM. NAME AS name, XGM.BAN_PRICE AS banPrice, XGM.BRAND_ID AS brandId, XB. NAME AS brandName, XGM.CLASSIFY_ID AS classifyId, XC. NAME AS classifyName, XC.MODEL AS model, XGM.UNIT AS unit, sc. NAME AS unitName, XGM.LOGISTCS AS logistcs, XGM.OBLIGATE1 AS obligate1, XGM. STATUS AS status, XGM.SPEC_PARAMETER AS specParameter, XGM.PARAMETER AS parameter, XGM.DETAIL AS detail, SK.ID AS stockId, ( CASE WHEN SK.CURRENT_STOCK IS NULL THEN 0 ELSE SK.CURRENT_STOCK END ) AS currentStock, ( CASE WHEN SK.USEABLE_STOCK IS NULL THEN 0 ELSE SK.USEABLE_STOCK END ) AS useableStock, XGM.REMARKS AS remarks, XGM.CREATE_USER AS createUserId, cu.NICK_NAME AS createUserName, XGM.LAST_CREATE_USER AS lastCreateUserId, lcu.NICK_NAME AS lastCreateUserName,
XL.ID AS lableId, XL.NAME AS lableName,
DATE_FORMAT( XGM.CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS createTime, DATE_FORMAT( XGM.LAST_CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS lastCreateTime FROM XH_GOODS_MAIN XGM LEFT JOIN XH_STOCK_MAIN SK ON SK.BASIC_ID = XGM.ID LEFT JOIN XH_SHOPS_MAIN XSM ON XSM.ID = XGM.SHOP_ID LEFT JOIN XH_BRAND XB ON XB.ID = XGM.BRAND_ID LEFT JOIN XH_CLASSIFY XC ON XC.ID = XGM.CLASSIFY_ID LEFT JOIN system_config sc ON sc.KEY_WORD = XGM.UNIT LEFT JOIN system_administrator cu ON cu.ID = XGM.CREATE_USER LEFT JOIN system_administrator lcu ON lcu.ID = XGM.LAST_CREATE_USER
INNER JOIN XH_GOODS_LABLE XGL ON XGL.GOODS_ID = XGM.ID LEFT JOIN XH_LABLE XL ON XL.ID = XGL.LABLE_ID
WHERE 1=1
AND XC.MODEL = #{model}
AND XGM.STATUS = #{status}
AND XGM.IS_DELETE = #{isDel}
AND XGM.ID = #{id}
AND XGM.BRAND_ID = #{brandId}
AND XGM.CLASSIFY_ID = #{classifyId}
AND XGM.OBLIGATE1 = #{obligate1}
AND XL.VALUE = #{lableValue}
AND XGM.SHOP_ID = #{shopId}
AND XGM. NAME LIKE CONCAT('%', #{name}, '%')
AND (lower(XGM. NAME) LIKE CONCAT('%', lower(#{wxQuery}), '%') OR lower(XB.NAME) LIKE CONCAT('%', lower(#{wxQuery}), '%'))
AND XGM.ID in
#{item.basicId}
AND XGM.ID NOT in
#{item.goodsId}
ORDER BY XB.NAME DESC,XGM.LAST_CREATE_TIME DESC,XC.MODEL DESC

SELECT XAB.ID AS bllId, XAB.FD_MODEL AS model, XAB.PARAMETER AS bllParameter, XAB.PRICE AS bllPrice, XAB.TIPS AS bllTips, XGM.LOGISTCS AS logIstcs, XGM.IS_DELETE AS del, XGM.ID AS goodsId, XGM. NAME AS goodsName, XGM.SPEC_PARAMETER AS goodsParameter, XGM.BAN_PRICE AS banPrice, XGM.SHOP_ID AS shopid, sa.FD_URL AS url, su.ID AS userId, su.NICK_NAME AS nickName, DATE_FORMAT( XAB.CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS createTime, DATE_FORMAT( XAB.LAST_CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS lastCreateTime FROM XH_REPLACEMENT_CAR XAB LEFT JOIN XH_GOODS_MAIN XGM ON XGM.ID = XAB.GOODS_ID LEFT JOIN system_user su ON su.OPENID = XAB.CREATE_USER LEFT JOIN system_attachment sa ON sa.FD_REF_ID = XGM.ID WHERE 1=1
AND XAB.ID = #{id}
AND XAB.CREATE_USER = #{openId}
AND XAB.ID in
#{item}
GROUP BY XAB.ID ORDER BY XAB.LAST_CREATE_TIME DESC

SELECT XB.ID AS id, XB. NAME AS name, XB.OFFICIAL_WEBSITE AS officialWebsite, XB.REMARKS AS remarks, XB.CREATE_USER AS createUserId, cu.NICK_NAME AS createUserName, XB.LAST_CREATE_USER AS lastCreateUserId, lcu.NICK_NAME AS lastCreateUserName, DATE_FORMAT( XB.CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS createTime, DATE_FORMAT( XB.LAST_CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS lastCreateTime FROM XH_BRAND XB LEFT JOIN system_administrator cu ON cu.ID = XB.CREATE_USER LEFT JOIN system_administrator lcu ON lcu.ID = XB.LAST_CREATE_USER WHERE 1=1
AND XB.ID = #{id}
ORDER BY XB.NAME DESC,XB.LAST_CREATE_TIME DESC

SELECT XB.ID AS id, XB.NAME AS label, DATE_FORMAT( XB.CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS createTime FROM XH_BRAND_CLASSIFY XB WHERE 1=1
AND XB.ID = #{id}
AND XB.NAME = #{label}
ORDER BY XB.CREATE_TIME DESC

SELECT XB.BRAND_ID AS id, XB.CLASS_ID AS parentId, XBR.NAME AS label, XBC.NAME AS className FROM XH_BRAND_CORR XB LEFT JOIN XH_BRAND XBR ON XBR.ID = XB.BRAND_ID LEFT JOIN XH_BRAND_CLASSIFY XBC ON XBC.ID = XB.CLASS_ID WHERE 1=1
AND XB.BRAND_ID = #{id}
AND XB.CLASS_ID = #{classId}

mapper.update:
UPDATE XH_BRAND_CLASSIFY XB


        XB.NAME = #{name},


WHERE XB.ID = #{id}

mapper.select:
SELECT XGL.LABLE_ID AS lableId, XGL.GOODS_ID AS goodsId FROM XH_GOODS_LABLE XGL WHERE 1=1
AND XGL.LABLE_ID = #{lableId}
AND XGL.GOODS_ID = #{goodsId}

SELECT XO.GOODS_ID AS goodsId, XG. NAME AS goodsName, XO. NO AS NO, XO.TYPES AS types, XO.STATUS AS status, XO.REF_NO AS ref_no, su.NICK_NAME AS nick_name, sa.NICK_NAME AS revierer_nick_name, su.ID AS user_id, XO.CONTENT AS content, XO.STAR AS star, XO.ORDER_ID AS orderId, XOH.NUMBER AS number, DATE_FORMAT( XO.CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS createTime FROM XH_ORDER_COMMENT XO LEFT JOIN system_user su ON su.ID = XO.REVIERER LEFT JOIN XH_GOODS_MAIN XG ON XG.ID = XO.GOODS_ID LEFT JOIN XH_ORDER_H XOH ON XOH.ID = XO.ORDER_ID LEFT JOIN system_administrator sa ON sa.ID = XO.REVIERER WHERE 1=1
AND XO.GOODS_ID = #{goodsId}
AND XO.ORDER_ID = #{orderId}
AND XO.TYPES = #{type}
AND XO.STATUS = #{status}

SELECT XHG.ID AS id, XHG.OPEN_ID AS openId, XHG.GOODS_ID AS goodsId, XHG.MODEL AS model FROM XH_HOME_GOODS XHG WHERE 1=1
AND XHG.ID = #{id}
AND XHG.MODEL = #{model}
AND XHG.OPEN_ID = #{openId}

mapper.update:
UPDATE XH_HOME_GOODS XHG


        XHG.GOODS_ID = #{goodsId},


WHERE XHG.ID = #{id}
