mapper.select:
SELECT XGM.ID AS id, XGM. CODE AS CODE, XGM.SHOP_ID AS shopId, XSM. NAME AS shopName, XGM. NAME AS name, XGM.BAN_PRICE AS banPrice, XGM.BRAND_ID AS brandId, XB. NAME AS brandName, XGM.CLASSIFY_ID AS classifyId, XC. NAME AS classifyName, XGM.MODEL AS model, XGM.UNIT AS unit, sc. NAME AS unitName, XGM.LOGISTCS AS logistcs, XGM. STATUS AS status, XGM.SPEC_PARAMETER AS specParameter, ( CASE WHEN SK.CURRENT_STOCK IS NULL THEN 0 ELSE SK.CURRENT_STOCK END ) AS currentStock, ( CASE WHEN SK.USEABLE_STOCK IS NULL THEN 0 ELSE SK.USEABLE_STOCK END ) AS useableStock, XGM.REMARKS AS remarks, XGM.CREATE_USER AS createUserId, cu.NICK_NAME AS createUserName, XGM.LAST_CREATE_USER AS lastCreateUserId, lcu.NICK_NAME AS lastCreateUserName, DATE_FORMAT( XGM.CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS createTime, DATE_FORMAT( XGM.LAST_CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS lastcreateTime FROM XH_GOODS_MAIN XGM LEFT JOIN XH_STOCK_MAIN SK ON SK.BASIC_ID = XGM.ID LEFT JOIN XH_SHOPS_MAIN XSM ON XSM.ID = XGM.SHOP_ID LEFT JOIN XH_BRAND XB ON XB.ID = XGM.BRAND_ID LEFT JOIN XH_CLASSIFY XC ON XC.ID = XGM.CLASSIFY_ID LEFT JOIN system_config sc ON sc.KEY_WORD = XGM.UNIT LEFT JOIN system_administrator cu ON cu.ID = XGM.CREATE_USER LEFT JOIN system_administrator lcu ON lcu.ID = XGM.LAST_CREATE_USER WHERE 1=1
AND XGM.MODEL = #{model}
AND XGM.ID = #{id}
AND XGM.BRAND_ID = #{brandId}
AND XGM. NAME = #{name}
ORDER BY XB.NAME DESC,XGM.LAST_CREATE_TIME DESC,XGM.MODEL DESC

SELECT XB.ID AS id, XB. NAME AS name, XB.OFFICIAL_WEBSITE AS officialWebsite, XB.REMARKS AS remarks, XB.CREATE_USER AS createUserId, cu.NICK_NAME AS createUserName, XB.LAST_CREATE_USER AS lastCreateUserId, lcu.NICK_NAME AS lastCreateUserName, DATE_FORMAT( XB.CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS createTime, DATE_FORMAT( XB.LAST_CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS lastcreateTime FROM XH_BRAND XB LEFT JOIN system_administrator cu ON cu.ID = XB.CREATE_USER LEFT JOIN system_administrator lcu ON lcu.ID = XB.LAST_CREATE_USER WHERE 1=1
AND XB.ID = #{id}
ORDER BY XB.NAME DESC,XB.LAST_CREATE_TIME DESC
