mapper.select:
SELECT XGM.ID AS id, XGM. CODE AS CODE, XGM.OUT_CODE AS outCode, XGM.SHOP_ID AS shopId, XSM. NAME AS shopName, XGM. NAME AS name, XGM.BRAND_ID AS brandId, XB. NAME AS brandName, XGM.CLASSIFY_ID AS classifyId, XC. NAME AS classifyName, XGM.MODEL AS model, XGM.UNIT AS unit, sc. NAME AS unitName, XGM.LOGISTCS AS logistcs, XGM. STATUS AS STATUS, SK.CURRENT_STOCK AS currentStock, SK.USEABLE_STOCK AS useableStock, XGM.REMARKS AS remarks, XGM.CREATE_USER AS createUserId, cu.NICK_NAME AS createUserName, XGM.LAST_CREATE_USER AS lastCreateUserId, lcu.NICK_NAME AS lastCreateUserName, DATE_FORMAT( XGM.CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS createTime, DATE_FORMAT( XGM.LAST_CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS lastcreateTime FROM XH_GOODS_MAIN XGM INNER JOIN XH_STOCK_MAIN SK ON SK.BASIC_ID = XGM.ID AND SK.CURRENT_STOCK > 0 AND SK.USEABLE_STOCK > 0 LEFT JOIN XH_SHOPS_MAIN XSM ON XSM.ID = XGM.SHOP_ID LEFT JOIN XH_BRAND XB ON XB.ID = XGM.BRAND_ID LEFT JOIN XH_CLASSIFY XC ON XC.ID = XGM.CLASSIFY_ID LEFT JOIN XH_GOODS_SPEC XGS ON XGS.GOODS_ID = XGM.ID LEFT JOIN system_config sc ON sc.KEY_WORD = XGM.UNIT LEFT JOIN system_administrator cu ON cu.ID = XGM.CREATE_USER LEFT JOIN system_administrator lcu ON lcu.ID = XGM.LAST_CREATE_USER WHERE 1=1
AND XGM.MODEL = #{model}
AND XGM.ID = #{id}
AND XGS.GOODS_SPEC_VALUE = #{specValue}
ORDER BY XB.NAME DESC,XGM.LAST_CREATE_TIME DESC,XGM.MODEL DESC

SELECT XB.ID AS id, XB. NAME AS name, XB.OFFICIAL_WEBSITE AS officialWebsite, XB.REMARKS AS remarks, XB.CREATE_USER AS createUserId, cu.NICK_NAME AS createUserName, XB.LAST_CREATE_USER AS lastCreateUserId, lcu.NICK_NAME AS lastCreateUserName, DATE_FORMAT( XB.CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS createTime, DATE_FORMAT( XB.LAST_CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS lastcreateTime FROM XH_BRAND XB LEFT JOIN system_administrator cu ON cu.ID = XB.CREATE_USER LEFT JOIN system_administrator lcu ON lcu.ID = XB.LAST_CREATE_USER WHERE 1=1
AND XB.ID = #{id}
ORDER BY XB.NAME DESC,XB.LAST_CREATE_TIME DESC
