mapper.select:
SELECT
    XO.ID AS id,
    XO.NUMBER AS number,
    XO.TYPE AS type,
    XO.SHOP_ID AS shopId,
    XO.PAY_STATUS AS payStatus,
    XO.ORDER_STATUS AS orderStatus,
    XO.DELIVERY_STATUS AS deliveryStatus,
    XO.PRICE AS price,
    XR.TYPE AS recovery,
    XO.ADDRESS AS address,
    su.NICK_NAME AS buyer,
    XI.TITLE AS invoiceTitle,
    XI.CONTENT AS invoiceContent,
    su.ID AS buyerId,
    XO.CREATE_TIME AS createTime
FROM
  XH_ORDER_H XO
INNER JOIN system_user su ON su.ID = XO.BUYER
LEFT JOIN XH_RECOVERY XR ON XR.ID = XO.RECOVERY
LEFT JOIN XH_INVOICE XI ON XI.ORDER_ID = XO.ID
WHERE 1=1

    AND XO.NUMBER = #{number}


    AND XO.PAY_STATUS = #{payStatus}


    AND XO.ORDER_STATUS = #{orderStatus}


    AND XO.ORDER_STATUS ${orderStatusT}


    AND XO.DELIVERY_STATUS = #{deliveryStatus}


    AND su.openId = #{openId}


    AND XO.ID = #{id}


    AND XO.LAST_CREATE_TIME <= #{lastCreateTime}


    AND XO.BUYER = #{buyer}


    AND XO.SHOP_ID = #{shopId}


    AND XO.CREATE_TIME between #{startTime} and #{endTime}

ORDER BY XR.TYPE ASC

SELECT XO.ID AS id, XO.NO AS no, XGM.ID AS goodsId, XGM. NAME AS goodsName, XGM.BAN_PRICE AS banPrice, XC.MODEL AS goodsModel, XO.PARAMETER AS parameter, XO.OLD_CHECK_ID AS oldcheck, XO.CHECK_STATUS AS checkStatus, XO.REMARKS AS remarks, XO.CREATE_TIME AS createTime FROM XH_ORDER_SALE_P XO LEFT JOIN XH_GOODS_MAIN XGM ON XGM.ID = XO.GOODS_ID LEFT JOIN XH_CLASSIFY XC ON XC.ID = XGM.CLASSIFY_ID LEFT JOIN XH_OLD_CHECK XOC ON XOC.ORDER_NUMBER = XO.ID AND XOC.NO = XO.NO WHERE 1=1
AND XO.ID = #{id}
AND XO.NO = #{no}
AND XO.CHECK_STATUS = #{checkStatus}
AND XOC.IS_DELETE = #{checkDel}
AND XC.MODEL = #{model}

SELECT XO.GOODS_ID AS goodsId, XO.NO AS no, XO.TYPES AS types, XO.STATUS AS status, XO.REF_NO AS ref_no, XO.REVIERER AS revierer, XO.CONTENT AS content, XO.STAR AS star, XO.ORDER_ID AS orderId, XO.REMARKS AS remarks, DATE_FORMAT( XO.CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS createTime FROM XH_ORDER_COMMENT XO WHERE 1=1
AND XO.GOODS_ID = #{goodsId}
AND XO.NO = #{no}
AND XO.STATUS = #{status}

mapper.update:
UPDATE XH_ORDER_COMMENT XO


        XO.STATUS = #{status},


WHERE XO.GOODS_ID = #{goodsId} AND XO.NO = #{no}

mapper.select:
SELECT XO.ID AS id, XH.NUMBER AS number, XH.PAY_STATUS AS payStatus, XH.ORDER_STATUS AS orderStatus, su.NICK_NAME AS buyer, XO.SHOP_ID AS shopId, XO.NO AS no, XO.AUDIT AS audit, XO.STATUS AS status, XH.PRICE AS price, XO. REASON AS reason, DATE_FORMAT( XO.CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS createTime FROM XH_ORDER_RETURN_H XO INNER JOIN system_user su ON su.ID = XO.CREATE_USER LEFT JOIN XH_ORDER_H XH ON XH.ID = XO.ID WHERE 1 = 1
AND XO.ID = #{id}
AND XO.SHOP_ID = #{shopId}
AND XO.NUMBER = #{number}
AND XO.STATUS = #{status}
AND XH.PAY_STATUS = #{payStatus}
AND XH.ORDER_STATUS = #{orderStatus}
