mapper.select:
SELECT XO.ID AS id, XO.NUMBER AS number, XO.TYPE AS type, XO.PAY_STATUS AS payStatus, XO.ORDER_STATUS AS orderStatus, XO.DELIVERY_STATUS AS deliveryStatus, XO.PRICE AS price, XO.CREATE_TIME AS createTime FROM XH_ORDER_H XO INNER JOIN system_user su ON su.ID = XO.BUYER WHERE 1=1
AND XO.NUMBER = #{number}
AND XO.PAY_STATUS = #{payStatus}
AND XO.ORDER_STATUS = #{orderStatus}
AND XO.DELIVERY_STATUS = #{deliveryStatus}
AND su.openId = #{openId}

SELECT XO.ID AS id, XO.NO AS no, XGM.ID AS goodsId, XGM. NAME AS goodsName, XGM.BAN_PRICE AS banPrice, XC.MODEL AS goodsModel, XO.PARAMETER AS parameter, XO.OLD_CHECK_ID AS oldcheck, XO.CHECK_STATUS AS checkStatus, XO.REMARKS AS remarks, XO.CREATE_TIME AS createTime FROM XH_ORDER_SALE_P XO LEFT JOIN XH_GOODS_MAIN XGM ON XGM.ID = XO.GOODS_ID LEFT JOIN XH_CLASSIFY XC ON XC.ID = XGM.CLASSIFY_ID WHERE 1=1
AND XO.ID = #{id}
AND XO.NO = #{no}
AND XO.CHECK_STATUS = #{checkStatus}
AND XC.MODEL = #{model}
