mapper.select:
SELECT
  st.ADMINISTRATOR_ID as administratorId,
  st.TOKEN as token,
  st.EXPTIME as exptime,
  st.REFRESH_TOKEM as refreshToken
from system_token  st
WHERE 1=1

    AND st.ADMINISTRATOR_ID = #{administratorId}


    AND st.REFRESH_TOKEM = #{refresh}


    AND st.TOKEN = #{token}

mapper.update:
UPDATE system_token


        TOKEN = #{token},


        EXPTIME = #{exptime,jdbcType=TIMESTAMP},


        REFRESH_TOKEM = #{refreshToken}


WHERE REFRESH_TOKEM = #{refresh}

mapper.select:
SELECT sw.ID AS id, sw.NAME AS name, sw.type AS type, sc.NAME AS typeName, sw.IS_SWITCH AS isSwitch, DATE_FORMAT(sw.CREATE_TIME,'%Y-%m-%d %H:%i:%S') AS createTime, DATE_FORMAT(sw.UPDATE_TIME,'%Y-%m-%d %H:%i:%S') AS updateTime FROM system_switch sw LEFT JOIN system_config sc on sc.KEY_WORD = sw.TYPE WHERE 1=1
AND sw.TYPE = #{type}
ORDER BY sw.IS_SWITCH DESC
