mapper.select:
SELECT su.ID AS id, su.NICK_NAME AS nickName, su.HEAD_PORTRAIT AS headPortrait, su.USER_NAME AS userName, su.OPENID AS openId, su.PHONE AS phone, su.STATUS AS status, DATE_FORMAT(su.CREATE_TIME,'%Y-%m-%d %H:%i:%S') AS createTime, DATE_FORMAT(su.UPDATE_TIME,'%Y-%m-%d %H:%i:%S') AS updateTime, su.IS_VALID AS isValid, su.REMARK AS remark FROM system_user su WHERE 1=1
AND su.ID = #{id}
AND su.USER_NAME = #{userName}
AND su.OPENID = #{openId}
