mapper.select:
SELECT sa.ID AS id, sa.NICK_NAME AS nickName, sat.FD_URL AS headPortrait, sa.USER_NAME AS userName, sa.PHONE AS phone, sa.EMAIL AS email, sa.WEI_XIN AS weiXin, sa.QQ AS qq, sa.NAME AS name, sa.ID_CARD AS idCard, sa.STATUS AS status, sc.NAME AS statusName, DATE_FORMAT(sa.CREATE_TIME,'%Y-%m-%d %H:%i:%S') AS createTime, DATE_FORMAT(sa.UPDATE_TIME,'%Y-%m-%d %H:%i:%S') AS updateTime, sa.IS_VALID AS isValid FROM system_administrator sa LEFT JOIN system_config sc ON sc.KEY_WORD = sa. STATUS LEFT JOIN system_token st on sa.ID = st.ADMINISTRATOR_ID LEFT JOIN system_attachment sat ON sat.ID = sa.HEAD_PORTRAIT WHERE 1=1
AND sa.ID = #{id}
AND sa.USER_NAME LIKE CONCAT('%', #{userName}, '%')
AND sa.NICK_NAME LIKE CONCAT('%', #{nickName}, '%')
AND sa.PHONE LIKE CONCAT('%', #{phone}, '%')
AND sa.STATUS = #{status}
ORDER BY sa.IS_VALID ASC
,#{sort}

mapper.update:
UPDATE system_administrator sa


        sa.IS_VALID = #{isValid},


WHERE sa.ID = #{id}

mapper.select:
SELECT sp.ID AS id, sp.TYPE AS type, sp.MODULE AS moduleKey, sc.NAME AS moduleName, sp.NAME AS name, sp.DESCRIBE AS depict, sp.IS_ENABLE AS isEnable, DATE_FORMAT(sp.CREATE_TIME,'%Y-%m-%d %H:%i:%S') AS createTime FROM system_permission sp INNER JOIN system_config sc on sp.MODULE = sc.KEY_WORD WHERE 1=1
AND sp.ID = #{id}
AND sp.MODULE = #{moduleKey}
AND sp.DESCRIBE LIKE CONCAT('%', #{depict}, '%')
ORDER BY sp.IS_ENABLE ASC, sp.MODULE ASC
,#{sort}

mapper.delete:
DELETE FROM administrator_permission WHERE 1=1

    AND ADMINISTRATOR_ID = #{userId} AND PERMISSION_ID = #{permissionId}


    AND ADMINISTRATOR_ID = #{userId}

mapper.select:
SELECT XR.ID AS id, XR.TYPE AS types, XR.IS_VALID AS isValid, DATE_FORMAT( XR.CREATE_TIME,'%Y-%m-%d %H:%i:%S') AS createTime FROM XH_RECOVERY XR WHERE 1=1
AND XR.TYPE LIKE CONCAT('%', #{types}, '%')
AND XR.IS_VALID = #{isValid}

mapper.update:
UPDATE XH_RECOVERY XR


        XR.TYPE = #{types},


WHERE XR.ID = #{id}

UPDATE XH_RECOVERY_USER XR


        XR.RECOVERY_ID = #{recoveryId},


WHERE XR.ID = #{id}
